<?phpnamespace Lite\Core;use Lite\DB\Model;use function Lite\func\array_clear_null;use function Lite\func\array_unshift_assoc;use function Lite\func\dump;use function Lite\func\h;use function Lite\func\is_assoc_array;use function Lite\func\is_function;/** * 视图类，大部分方法用于在模版中直接调用 * 注意：部分方法由于继承于路由，调用这些方法的时候，需考虑是否做htmlspecialchars保护 * User: sasumi * Date: 2015/01/06 * Time: 9:49 */class View extends Router{	const EVENT_AFTER_VIEW_RENDER = 'EVENT_AFTER_VIEW_RENDER';	const REQ_PAGE = 'page';	const REQ_JSON = 'json';	const REQ_JSONP = 'jsonp';	const REQ_FORM_SENDER = 'formsender';	const REQ_IFRAME = 'iframe';	const REQ_DES_KEY = 'ref';	//选项转换成select控件数量，大于这个数量，options将被转换成select	//包括checkbox也会被转换成select[multiple]	public static $option_convert_count = 2;	//options数据失效显示文本	public static $option_data_invalid_text = '[该项数据已失效]';	private $req_type;	private static $pre_vars = array();	/** @var \Lite\Core\Result */	private $result;	private $ret_mode = false;	/**	 * 构造方法	 * @param mixed $data 视图渲染数据	 */	public function __construct($data=null){		if($data instanceof Result){			$this->result = $data;		} else if($data instanceof self){			dump($this, $data, 1);		} else {			//默认返回数组情况的标记为"成功"			$this->result = new Result('', true, $data);			$this->ret_mode = true;		}		$this->req_type = $this->parseRequestType();	}	/**	 * display sets	 * @param $value	 * @param $options	 * @param Model $model_instance	 * @return string	 * @internal param $params	 */	public static function displaySet($value, $options, Model $model_instance){		$vs = explode(',',$value);		$t = array();		foreach($vs as $v){			$t[] = $options[$v];		}		$html = '<ul class="tags"><li>'.join('</li><li>',$t).'</li></ul>';		return $html;	}	/**	 * 渲染搜索表单元素	 * @param $value	 * @param $field	 * @param $define	 * @param null $model_instance	 * @param array $extend_attr	 * @return mixed|string	 */	public static function renderSearchFormElement($value, $field, $define, $model_instance=null, $extend_attr=array()){		unset($define['rel']);		unset($define['default']);		unset($define['required']);		$extend_attr = array_merge(array(			'title' => $define['alias'],		), $extend_attr);		if(in_array($define['type'],['date', 'datetime', 'time', 'timestamp'])){			return static::renderDateRangeElement($value, $field, $define, $model_instance, $extend_attr);		}		if(in_array($define['type'], array('text', 'simple_rich_text', 'rich_text'))){			$define['type'] = 'string';		}		if($define['options']){			$define['options'] = is_callable($define['options']) ? call_user_func($define['options']) : $define['options'];			array_unshift_assoc($define['options'], '', '全部'.$define['alias']);		}		$extend_attr = array_merge(array('placeholder'=>$define['alias']),$extend_attr);		return static::renderFormElement($value, $field, $define, $model_instance, $extend_attr, false);	}	/**	 * 渲染日期范围选择	 * @param $value_range	 * @param $field	 * @param $define	 * @param null $model_instance	 * @param array $extend_attr	 * @return string	 */	public static function renderDateRangeElement($value_range, $field, $define, $model_instance=null, $extend_attr=array()){		list($st, $et) = $value_range;		$org_pl = $extend_attr['placeholder'];		unset($extend_attr['placeholder']);		$rel = $define['rel'];		$start_input = static::buildElement('input', array_merge(array(			'type' => 'text',			'rel' => $rel,			'name' => $field.'[]',			'value' => $st,			'text' => $st,			'placeholder' => $org_pl.'开始'		), $extend_attr), $define);		$end_input = static::buildElement('input', array_merge(array(			'type' => 'text',			'rel' => $rel,			'name' => $field.'[]',			'value' => $et,			'text' => $et,			'placeholder' => $org_pl.'结束'		), $extend_attr), $define);		return $start_input.' - '.$end_input;	}	/**	 * 渲染字段	 * renderFormElement别名方法	 * @param $field	 * @param Model $model_instance	 * @param array $extend_attr	 * @param bool|true $add_default_selection	 * @return string	 */	public static function renderField($field, Model $model_instance, $extend_attr=array(), $add_default_selection=true){		$val = $model_instance->$field;		$def = $model_instance->getPropertiesDefine($field);		return static::renderFormElement($val, $field, $def, $model_instance, $extend_attr, $add_default_selection);	}	/**	 * get option text	 * @param $val	 * @param array $options	 * @param bool $use_invalid_text	 * @return null	 */	private static function __getOptionTextByVal($val, array $options, $use_invalid_text=false){		if(count($options) != count($options, COUNT_RECURSIVE)){			foreach($options as $gn=>$opts){				foreach($opts as $k=>$n){					if($k == $val){						return $n;					}				}			}		} else {			foreach($options as $k=>$n){				if($k == $val){					return $n;				}			}		}		if($use_invalid_text){			return static::$option_data_invalid_text;		}		return null;	}	/**	 * 渲染表单元素	 * @param $value	 * @param string $field	 * @param array $define	 * @param Model|null $model_instance 实例对象	 * @param array $extend_attr 扩展属性	 * @param bool $add_default_selection 是否添加默认选择选项	 * @return string	 */	public static function renderFormElement($value, $field, $define, $model_instance=null, $extend_attr=array(), $add_default_selection=true){		$rel = $define['rel'];		$required = $define['required'];		$readonly = $define['readonly'];		$disabled = $define['disabled'];		//default value		if(isset($define['default']) && !isset($value)){			$value = $define['default'];		}		//form		if(is_callable($define['form'])){			return call_user_func($define['form'], $value, $model_instance);		}		//transform closure options to array		if(is_callable($define['options'])){			$define['options'] = call_user_func($define['options'], $model_instance);		}		if(is_callable($define['group_options'])){			$define['group_options'] = call_user_func($define['group_options'], $model_instance);		}		//禁用选项		if($disabled){			$extend_attr['disabled'] = 'disabled';		}		//只读		if($readonly){			$extend_attr['readonly'] = 'readonly';		}		//可选项表单（包括checkbox、select、radio）		//optional field define		if($define['options']){			//集合控件特殊处理			$is_set = $define['type'] == 'set';			//使用select组件			if($define['group_options'] || count($define['options']) > static::$option_convert_count){				//处理选项只读情况				if($readonly){					$html = '<input type="hidden" value="'.$value.'" name="'.$field.'"/>';					$html .= static::buildElement('input', array(						'readonly' => 'readonly',						'type' => 'text',						'value' => self::__getOptionTextByVal($value, $define['options'], true)					), $define);					return $html;				}				$extend_attr_str = static::buildAttributesStr($extend_attr);				$html = '<select size="1" name="'.$field.'"'					.($required ? ' required="required"' : '')					.($is_set ? ' multiple="multiple"' : '')					.$extend_attr_str.'>';				if($add_default_selection && !$required || true){					$html .= static::buildElement('option', array(						'value' => '',						'text' => '请选择'					), $define);				}				//数据失效处理				if($value !=null && strlen($value) && !isset($define['options'][$value])){					$html .= static::buildElement('option', array(						'value' => $value,						'text' => static::$option_data_invalid_text,						'selected' => 'selected',						'class' => 'option-disabled'					), $define);				}				if($define['group_options']){					foreach($define['group_options'] as $group_name=>$options){						$html .= '<optgroup label="'.h($group_name).'">';						foreach($options as $k=>$n){							$attr = array('value' => $k,'text' => $n);							if((string)$value === (string)$k || (isset($define['default']) && $define['default'] == $k)){								$attr['selected'] = 'selected';							}							$html .= static::buildElement('option', array_merge($attr, $extend_attr), $define);						}						$html .= '</optgroup>';					}				} else {					foreach($define['options'] as $k=>$n){						$attr = array('value' => $k,'text' => $n);						if((string)$value === (string)$k || (isset($define['default']) && $define['default'] == $k)){							$attr['selected'] = 'selected';						}						$html .= static::buildElement('option', array_merge($attr, $extend_attr), $define);					}				}				$html .= '</select>';			} else {				$html = '';				foreach($define['options'] as $k=>$n){					$attr = array(						'type' => $is_set ? 'checkbox' : 'radio',						'rel' => $rel,						'name' => $field,						'value' => $k					);					if((string)$value == (string)$k || (!strlen($value) && isset($define['default']) && $define['default'] == $k)){						$attr['checked'] = 'checked';					}					//数据失效处理					if($value !=null && strlen($value) && !isset($define['options'][$value])) {						$attr['text'] = static::$option_data_invalid_text;					}					$html .= '<label>'.static::buildElement('input', array_merge($attr, $extend_attr), $define);					$html .= $n;					$html .= '</label>';				}			}			return $html;		}		switch($define['type']){			case 'text':			case 'simple_rich_text':			case 'rich_text':				$html = static::buildElement('textarea', array_merge(array(					'type' => 'text',					'rel' => $rel,					'name' => $field,					'text' => $value				), $extend_attr), $define);				break;			case 'int':			case 'float':			case 'double':				$attr = array(					'type' => 'number',					'name' => $field,					'rel' => $rel,					'value' => $value				);				if($define['type'] == 'int'){					$attr['step'] = 1;				}				else if($define['precision']){					$attr['step'] = '0.'.str_repeat('0', $define['precision']-1).'1';				}				if(isset($define['min'])){					$attr['min'] = $define['min'];				}				if(isset($define['max'])){					$attr['max'] = $define['max'];				}				$html = static::buildElement('input', array_merge($attr, $extend_attr), $define);				break;			case 'file':				$html = static::buildElement('input', array_merge(array(					'type' => 'file',					'rel' => $rel,					'name' => $field,					'value' => $value				), $extend_attr), $define);				break;			case 'password':				$attr = array(					'type' => 'password',					'rel' => $rel,					'name' => $field,					'value' => $value,				);				$pattern = self::getPattern($define);				if($pattern){					$attr['pattern'] = $pattern;				}				$html = static::buildElement('input', array_merge($attr, $extend_attr), $define);				break;			default:				$attr = array(					'type' => 'text',					'rel' => $rel,					'name' => $field,					'value' => $value				);				$pattern = self::getPattern($define);				if($pattern){					$attr['pattern'] = $pattern;				}				$html = static::buildElement('input', array_merge($attr, $extend_attr), $define);				break;		}		return $html;	}	/**	 * 获取html pattern	 * @param $define	 * @return string	 */	private static function getPattern($define){		switch($define['type']){			case 'datetime':				return'^\d{4}-\d{1,2}-\d{1,2}\s\d{1,2}:\d{1,2}:\d{1,2}$';			case 'date':				return '^\d{4}\-\d{1,2}\-\d{1,2}$';			case 'time':				return '^\d{1,2]:\d{1,2}:\d{1,2}$';		}		return '';	}	/**	 * 构建元素属性	 * @param $tag	 * @param array $attributes	 * @param array $define	 * @return string	 */	public static function buildElement($tag, $attributes=array(), $define=array()){		$tag = strtolower($tag);		$single_tag = in_array($tag, array('input', 'img'));		$html = "<$tag ";		$attributes = array_clear_null($attributes);		$text = addslashes($attributes['text']);		unset($attributes['text']);		$html .= static::buildAttributesStr($attributes);		if($define['required'] && $tag != 'option'){			$html .= ' required="required"';		}		$html .= $single_tag ? "/>" : ">".$text."</$tag>";		return $html;	}	/**	 * 构建html属性字符串	 * @param array $attributes	 * @return string	 */	protected static function buildAttributesStr($attributes=array()){		$html = '';		foreach($attributes as $k=>$v){			if($k == 'pattern'){				$html .= " $k=\"".$v."\"";			} else {				$html .= " $k=\"".addslashes($v)."\"";			}		}		return $html;	}	/**	 * 遍历显示的字段	 * @param $callback callback(alias, value, field_name)	 * @param \Lite\DB\Model $model_instance	 * @param array $fields	 */	public static function walkDisplayProperties(callable $callback, Model $model_instance=null, $fields=array()){		if(!$model_instance){			return;		}		$defines = $model_instance->getPropertiesDefine();		if($fields){			foreach($fields as $field){				$alias = $defines[$field]['alias'];				if($alias){					call_user_func($callback, $alias, static::displayField($field, $model_instance), $field);				}			}		} else {			foreach($defines as $field=>$define){				if(!$define['primary']){					$alias = $defines[$field]['alias'];					if($alias){						call_user_func($callback, $alias, static::displayField($field, $model_instance), $field);					}				}			}		}	}	/**	 * display field	 * @param string $field	 * @param Model $model_instance	 * @return string	 */	public static function displayField($field, Model $model_instance){		$define = $model_instance->getPropertiesDefine($field);		$value = $model_instance->$field;		if($define['display']){			if(is_callable($define['display'])){				$define['display'] = call_user_func($define['display'], $model_instance);			}			return $define['display'];		}		if($define['options']){			if(is_callable($define['options'])){				$define['options'] = call_user_func($define['options'], $model_instance);			}			if($define['type'] == 'set'){				return static::displaySet($value, $define['options'], $model_instance);			} else {				$value = $define['options'][$value];			}		}		switch($define['type']){			//hide password display in form field			case 'password':				return '******';			case 'text':			case 'float':			case 'double':			case 'int':				return $value;			case 'file':				return $value;			case 'date':			case 'datetime':			case 'timestamp':				if(strpos($value, '0000-00-00') !== false){					return '-';				}				return $value;			case 'microtime':				if($value == 0){					return '-';				}				list($t, $s) = explode('.', $value);				$s = (int)$s;				return date('Y-m-d H:i:s', $t). ($s ? ' '.$s : '');			case 'set':			case 'enum':			default:				return $value;		}	}	/**	 * 渲染视图数据	 * @param string||array $key	 * @param string $val	 */	public function assign($key, $val=null){		if(isset($val)){			$this->result->setItem($key, $val);		} else {			foreach($key as $k=>$v){				$this->result->setItem($k, $v);			}		}	}	/**	 * 预渲染视图数据，用于系统全局前置变量的渲染	 * @param array $pre_vars	 */	public static function preAssignVar(array $pre_vars){		self::$pre_vars = array_merge(self::$pre_vars, $pre_vars);	}	/**	 * 获取前置视图变量	 * @return array	 */	public static function getPreVar(){		return self::$pre_vars;	}	/**	 * 解析请求类型	 * @return string	 */	private function parseRequestType(){		$type = Router::get(self::REQ_DES_KEY);		if(empty($type) || !in_array($type, array(self::REQ_FORM_SENDER , self::REQ_JSON, self::REQ_JSONP, self::REQ_IFRAME, self::REQ_PAGE))){			$type = self::REQ_PAGE;		}		return $type;	}	/**	 * 获取请求类型	 * @return string	 */	public function getRequestType(){		return $this->req_type;	}	/**	 * 设置渲染数据	 * @param Result $result	 */	public function setResult(Result $result){		$this->result = $result;	}	/**	 * 获取渲染的数据	 * @return Result	 */	public function getResult(){		return $this->result;	}	/**	 * 获取数据	 * @param null $key	 * @return array	 */	public function getData($key=null){		$data = array();		if($this->result->getData()){			$data = array_merge(self::$pre_vars, $this->result->getData());		}		if(!$key){			return $data;		}		return $data[$key];	}	/**	 * 渲染模版	 * @param string $file 文件名称	 * @param bool $return 是否以返回方式返回渲染结果	 * @param null $req_type 请求类型，缺省采用自动解析请求类型	 * @return string	 */	public function render($file=null, $return=false, $req_type=null){		$result = $this->result;		$jump_url = $result->getJumpUrl();		$message = $result->getMessage();		$html = '';		if($req_type === null || !in_array($req_type, array(self::REQ_FORM_SENDER , self::REQ_JSON, self::REQ_JSONP, self::REQ_IFRAME, self::REQ_PAGE))){			$req_type = $this->req_type;		}		switch($req_type){			case self::REQ_FORM_SENDER:				$html = $result->getIframeResponse();				break;			case self::REQ_JSON:				$html = $result->getJson();				break;			case self::REQ_JSONP:				$html = $result->getJsonp();				break;			case self::REQ_PAGE:			case self::REQ_IFRAME:			default:				$data = $result->getData();				//support pure string mode				if(is_scalar($data)){					echo $data;					break;				}				$file = static::resolveTemplate($file);				if($file){					ob_start();					if(is_array(self::$pre_vars)){						extract(self::$pre_vars);					}					if(is_array($data)){						extract($data);					}					include $file;					$html = ob_get_contents();					ob_clean();				}				if($this->req_type == self::REQ_PAGE && $this->ret_mode){					//页面模式+纯数据,不补充js交互				} else if($message || $jump_url){					$html .= '<script>';					$html .= ($message?'alert("'.addslashes($message).'");':'');					$html .= ($jump_url ? 'location.href="'.addslashes($jump_url).'";' : '');					$html .= '</script>';				}				break;		}		//引用方式抛事件		$ref_param = new RefParam(array('html'=>$html, 'return'=>$return));		Hooker::fire(self::EVENT_AFTER_VIEW_RENDER, $ref_param);		if($return){			return $ref_param['html'];		} else {			echo $ref_param['html'];			return '';		}	}	/**	 * 获取模版文件路径	 * @param string $file_name	 * @return string 文件路径	 */	public static function resolveTemplate($file_name=null){		$tpl_path = Config::get('app/tpl');		if(is_file($file_name)){			return $file_name;		}		if($file_name){			$file_name = trim(str_replace(array('/','\\'), DS, $file_name), DS);			return is_file($tpl_path.$file_name) ? $tpl_path.$file_name : null;		}		$controller = self::getController();		$action = self::getAction();		if($controller == self::getDefaultController() &&			$action == self::getDefaultAction()){			$file = 'index.php';		} else if($action == self::getDefaultAction()){			$file = $controller.'.php';		} else {			$file = $controller.'_'.$action.'.php';		}		$file = Config::get('app/tpl').strtolower($file);		$file2 = Config::get('app/tpl').strtolower($controller.DS.$action).'.php';		return is_file($file) ? $file : (			is_file($file2) ? $file2 : ''		);	}	/**	 * 获取静态资源url，重载路由方法，保护html输出	 * @param string $file_name	 * @param null $type	 * @return string	 */	public static function getStaticUrl($file_name, $type = null){		$url = call_user_func_array('parent::getStaticUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 获取url，重载路由方法，保护html输出	 * @param string $target	 * @param array $params	 * @param null $router_mode	 * @return string	 */	public static function getUrl($target = '', $params = array(), $router_mode = null){		$url = call_user_func_array('parent::getUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 获取当前action页面url，重载路由方法，保护html输出	 * @param array $param	 * @return string	 */	public static function getCurrentActionPageUrl($param=array()){		$url = call_user_func_array('parent::getCurrentActionPageUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 获取js资源url，重载路由方法，保护html输出	 * @param string $file_name	 * @return string	 */	public static function getJsUrl($file_name){		$url = call_user_func_array('parent::getJsUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 调用css路径，重载路由方法，保护html输出	 * @param string $css	 * @return string	 */	public static function getCssUrl($css) {		$url = call_user_func_array('parent::getCssUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 调用img路径，重载路由方法，保护html输出	 * @param string $file_name	 * @return string	 */	public static function getImgUrl($file_name) {		$url = call_user_func_array('parent::getImgUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 调用flash路径，重载路由方法，保护html输出	 * @param string $file_name	 * @return string	 */	public static function getFlashUrl($file_name) {		$url = call_user_func_array('parent::getFlashUrl', func_get_args());		return htmlspecialchars($url);	}	/**	 * 获取脚本链接代码	 * @param string||array $js	 * @return string	 **/	public static function getJs($js/**, $js2, $js3...*/){		$args = func_get_args();		$rst = '';		foreach($args as $js){			if(gettype($js) == 'string'){				if(stripos('/', $js) === false){					$js = static::getJsUrl($js);				}				$rst .= '<script type="text/javascript" src="'.$js.'" charset="utf-8"></script>';			} else {				$sc = '<script type="text/javascript"';				foreach($js as $pro=>$val){					if(strtolower($pro) == 'src'){						$sc .= ' src="'.static::getJsUrl($val).'"';					} else {						$sc .= ' '.htmlspecialchars($pro).'="'.htmlspecialchars($val).'"';					}				}				$sc .= '></script>';				$rst .= $sc;			}		}		return $rst;	}	/**	 * 获取样式表链接代码	 * @param string||array $css	 * @return string	 **/	public static function getCss($css/**, $css2, $css3...*/){		$args = func_get_args();		$rst = '';		foreach($args as $css){			if(gettype($css) == 'string'){				$rst .= '<link rel="stylesheet" type="text/css" href="'.htmlspecialchars(static::getCssUrl($css)).'" media="all"/>';			} else {				$lnk = '<link rel="stylesheet" type="text/css"';				foreach($css as $pro=>$val){					if(strtolower($pro) == 'href'){						$lnk .= ' href="'.static::getCssUrl($val).'"';					} else {						$lnk .= ' '.htmlspecialchars($pro).'="'.htmlspecialchars($val).'"';					}				}				$lnk .= '/>';				$rst .= $lnk;			}		}		return $rst;	}	/**	 * 获取配置	 * @param $key	 * @return array|null	 */	public static function getConfig($key){		return Config::get($key);	}	/**	 * 获取IMG代码	 * @param string $src 图片src	 * @param array $option 选项	 * @return string	 */	public static function getImg($src, $option=array()){		if(!$src){			return '';		}		$ext = '';		$adjust = false;		$src = static::getImgUrl($src);		$option = array_merge(array(			'onload' => '(function(img){window.__img_adjust__ &&　__img_adjust__(img);})(this)',			'onerror' => '(function(img){window.__img_error__ &&　__img_error__(img);})(this)',		), $option);		$onload_fn = $option['onload'];		foreach($option as $key=>$val){			if(preg_match("/(min-height|min-width|max-height|max-width)/i", $key)){				$adjust = true;				$key = 'data-'.$key;			}			$ext .= ' '.htmlspecialchars($key).'="'.htmlspecialchars($val).'"';		}		return "<img src=\"".$src."\" $ext ".($adjust ? " onload=\"$onload_fn(this)\"" : "")."/>";	}}